import React, { PureComponent } from 'react';
import {
    LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend
} from 'recharts';
import '../components_css/contasapagar.css';
const data_sp = [
    {
        name: 'Janeiro', Taubate: 4000, 'Ribeirão Preto': 2400, Sorocaba: 1500,
    },
    {
        name: 'Fevereiro', Taubate: 3000, 'Ribeirão Preto': 1398, Sorocaba: 2210,
    },
    {
        name: 'Março', Taubate: 2000, 'Ribeirão Preto': 9800, Sorocaba: 2290,
    },
    {
        name: 'Abril', Taubate: 2780, 'Ribeirão Preto': 3908, Sorocaba: 2000,
    },
    {
        name: 'Maio', Taubate: 1890, 'Ribeirão Preto': 4800, Sorocaba: 7000,
    },
    {
        name: 'Junho', Taubate: 2390, 'Ribeirão Preto': 3800, Sorocaba: 2500,
    },
    {
        name: 'Julho', Taubate: 3490, 'Ribeirão Preto': 4300, Sorocaba: 2100,
    },
];

const data_sul = [
    {
        name: 'Janeiro', Gaspar: 4000, Curitiba: 2400, Londrina: 1500,
    },
    {
        name: 'Fevereiro', Gaspar: 3000, Curitiba: 1398, Londrina: 2210,
    },
    {
        name: 'Março', Gaspar: 2000, Curitiba: 9800, Londrina: 2290,
    },
    {
        name: 'Abril', Gaspar: 2780, Curitiba: 3908, Londrina: 2000,
    },
    {
        name: 'Maio', Gaspar: 1890, Curitiba: 4800, Londrina: 7000,
    },
    {
        name: 'Junho', Gaspar: 2390, Curitiba: 3800, Londrina: 2500,
    },
    {
        name: 'Julho', Gaspar: 3490, Curitiba: 4300, Londrina: 2100,
    },
];

export default class Example extends PureComponent {

    constructor() {
        super();

    }

    componentDidMount() {

    }


    render() {


        return (
            <div className="titulo container">
                <div>
                    <div className="row">

                        <div className="col-sm-8">
                            <div style={{ width: '100%', height: 300 }} id="sp">
                                <ResponsiveContainer>
                                    <LineChart
                                        data={data_sp}
                                        margin={{
                                            top: 5, right: 30, left: 20, bottom: 5,
                                        }}
                                    >
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="name" />
                                        <YAxis />
                                        <Tooltip />
                                        <Legend />
                                        <Line type="monotone" dataKey="Taubate" stroke="#8884d8" activeDot={{ r: 8 }} />
                                        <Line type="monotone" dataKey="Ribeirão Preto" stroke="black" activeDot={{ r: 8 }} />
                                        <Line type="monotone" dataKey="Sorocaba" stroke="green" activeDot={{ r: 8 }} />
                                    </LineChart>
                                </ResponsiveContainer>
                            </div>




                        </div>
                        <div className="col-sm-4 escolha_regiao" style={{marginTop: '1em'}}>
                            <p id="sp">São Paulo</p>
                            <p id="mg">Minas Gerais</p>
                            <p id="sc">Santa Catarina</p>
                            <p id="pr">Paraná</p>
                            <p id="ne">Nordeste</p>
                        </div>

                    </div>
                </div>


                <div style={{ width: '100%', height: 300 }} id="sul">
                    <ResponsiveContainer>
                        <LineChart
                            data={data_sul}
                            margin={{
                                top: 5, right: 30, left: 20, bottom: 5,
                            }}
                        >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Line type="monotone" dataKey="Gaspar" stroke="#8884d8" activeDot={{ r: 8 }} />
                            <Line type="monotone" dataKey="Curitiba" stroke="black" activeDot={{ r: 8 }} />
                            <Line type="monotone" dataKey="Londrina" stroke="green" activeDot={{ r: 8 }} />
                        </LineChart>
                    </ResponsiveContainer>
                </div>


            </div>
        );
    }
}
